(SELECT product.id, product.name, product.short_des, detail_des, category_id, producerId, product.img2, product.img1, product.slug, category.id as category_id, category.name as category_name, category.description as category_description, category.img as category_img, category.parent as category_parent, category.slug as category_slug, category.active as category_active, category.deleted as category_deleted, category.level as category_level, category.img_large as category_img_large FROM product JOIN category WHERE product.active = 1 AND product.deleted = 0 AND category_id = category.id AND category.id = 1) 
UNION 
(SELECT product.id, product.name, product.short_des, detail_des, category_id, producerId, product.img2, product.img1, product.slug, category.id as category_id, category.name as category_name, category.description as category_description, category.img as category_img, category.parent as category_parent, category.slug as category_slug, category.active as category_active, category.deleted as category_deleted, category.level as category_level, category.img_large as category_img_large FROM product JOIN category WHERE product.active=1 AND product.deleted = 0 AND category_id=category.id AND category.parent = 1) 
UNION 
(SELECT product.id, product.name, product.short_des, detail_des, category_id, producerId, product.img2, product.img1, product.slug, category.id as category_id, category.name as category_name, category.description as category_description, category.img as category_img, category.parent as category_parent, category.slug as category_slug, category.active as category_active, category.deleted as category_deleted, category.level as category_level, category.img_large as category_img_large FROM product JOIN category WHERE product.active=1 AND product.deleted = 0 AND category_id=category.id AND category.parent = 2)